        this.state={
            name:'Adityaraj singh',
            status:true,
            count:0
        }

    }

    increment(){
        this.setState({count:this.state.count + 1,
            status:!this.state.status
        })
   document.title=`clicked ${this.state.count} times`
    } 
   decrement(){
        this.setState({count:this.state.count - 1,
            status:!this.state.status
        })
        
    }


  
