const skip=(forwards=true)=>{
    
    document.getElementById('progresses').style.width='0%'
    audioEl.current.currentTime=0
     if(forwards){
        Setcurrentsongindex(()=>{
            Setinitialtime(0)
             let temp=currentsongindex
             temp=temp+1;

             if(temp>songs.length-1){
               
                 temp=0
             }        
             return temp
         })
        
     }
     else{
         Setcurrentsongindex(()=>{
             let temp=currentsongindex
             temp=temp-1;

             if(temp<0){
                 temp=songs.length-1
             }
                return temp
         })   
     } 
 
}


